import{r as a,j as e}from"./index-0742d672.js";import{P as m}from"./PageTitle-9d23e7a0.js";import{P as x}from"./PageSubtitle-01c65152.js";import{A as d,m as f}from"./index-22ebc310.js";const u=[{id:1,chapterTitle:"Способи оплати",chapterText:["Оплата карткою через систему LIQPAY.","Оплата товару в Новій Пошті при отриманні. Комісія Нової Пошти за переказ грошей 2% від вартості товару + 20 грн.","Готівковий розрахунок.","Оплатити товар готівкою можна в наших магазинах в Івано-Франківську.","Для оплати на розрахунковий рахунок телефонуйте менеджеру."]},{id:2,chapterTitle:"Доставка",chapterText:["Доставка здійснюється за допомогою служб: Укрпошта, Нова Пошта. При замовленні на суму більше 15 000 грн, доставка безкоштовна, крім комодів і шаф.","При оплаті післяплатою покупець оплачує комісію Нової Пошти за переказ грошей (2% від вартості товару + 20 грн).","При оплаті на поточний рахунок або через систему LIQPAY додаткових комісій немає. В інших випадках доставка товару здійснюється від імені покупця за тарифами Нової Пошти. Всі подробиці можна дізнатися у наших менеджерів.","Вартість доставки залежить від габаритів, ваги, місця призначення та типу доставки. Ви оплачуєте послуги перевізника з доставки замовлення на місце при отриманні.","Після отримання товару необхідно мати при собі номер декларації і посвідчення особи.При замовленні товару на юридичну особу одержувач повинен мати довіреність або довіреність від компанії-замовника в будь-якій формі з «мокрою» печаткою.","На складі служби доставки обов'язково перевірте вміст товару, наявність зовнішніх пошкоджень упаковки і дефектів товару. Ви маєте право відмовитися від отримання товару на місці без додаткових витрат з вашого боку."]},{id:3,chapterTitle:"Повернення товару",chapterText:["Відповідно до «Закону про захист прав споживачів» ви можете відмовитися від прийому товарів, придбаних на kolyska.com.ua, або обміняти їх на аналогічний.","Обмін або повернення товару хорошої якості може бути проведений протягом 14 днів з дати покупки (дата вказана в чеку) при дотриманні наступних умов:","    продукт не можна використовувати протягом усього періоду з моменту покупки - тобто новий;","   збереження товарного вигляду (відсутність подряпин, відколів, потертостей і т. д.), споживчих властивостей, пломб, етикеток придбаної продукції;","  наявність чека, отриманого споживачем з придбаним товаром.","Якщо в продажу немає аналогічного продукту, ви маєте право придбати інший продукт з існуючого асортименту з перерахунком вартості відповідно до «Закону про захист прав споживачів» або отримати відшкодування в розмірі вартості продукту. повернутий товар.","Прийміть до уваги! Якщо покупець розмістив замовлення на товар, доставка якого повинна бути проведена з іншого міста, в разі повернення товару по необ'єктивним причин (йому просто не сподобалося), покупець оплачує доставку товару. замовлення в обидві сторони згідно квитанції транспортної компанії.","Якщо повернення стався через заводський дефект або механічного пошкодження, покупець не буде платити за доставку повернення."]},{id:4,chapterTitle:"Комплектація товару",chapterText:["У рідкісних випадках опис групи товарів на сайті може не повністю відповідати наявності. Щоб уникнути незручних ситуацій уточнюйте у менеджера повний комплект замовлених вами товарів."]}];function C(){const[i,n]=a.useState(0),[s,r]=a.useState(!1),c=()=>{r(!s)};return a.useEffect(()=>{r(!0)},[i]),e.jsx("div",{className:"info-page",children:e.jsxs("div",{className:"container",children:[e.jsx(m,{text:"Інформація для наших покупців"}),e.jsx("ul",{className:"info-list",children:u.map(({id:t,chapterTitle:o,chapterText:l})=>e.jsxs("li",{children:[e.jsx("div",{onClick:()=>{n(t),c()},children:e.jsx(x,{text:o})}),e.jsx(d,{children:i===t&&s&&e.jsx("ul",{onClick:c,className:"chapter-list",children:l.map((p,h)=>e.jsx(f.li,{className:"chapter-item",initial:{opacity:0},animate:{opacity:1},exit:{opacity:0},transition:{duration:.5},children:p},h))})})]},t))})]})})}export{C as default}